INSERT INTO `locations` (`MaDD`, `tenDD`, `content`, `diaChi`, `image`, `createDate`) VALUES
(3, 'Hà Nội', '<p><strong>H&agrave; Nội</strong>&nbsp;l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%E1%BB%A7_%C4%91%C3%B4\">thủ đ&ocirc;</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_ph%E1%BB%91_tr%E1%BB%B1c_thu%E1%BB%99c_trung_%C6%B0%C6%A1ng_(Vi%E1%BB%87t_Nam)\">th&agrave;nh phố trực thuộc trung ương</a>&nbsp;v&agrave; cũng l&agrave; một&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%C3%B4_th%E1%BB%8B_Vi%E1%BB%87t_Nam\">đ&ocirc; thị loại đặc biệt</a>&nbsp;của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a>. H&agrave; Nội nằm về ph&iacute;a t&acirc;y bắc của trung t&acirc;m v&ugrave;ng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BB%93ng_b%E1%BA%B1ng_s%C3%B4ng_H%E1%BB%93ng\">đồng bằng ch&acirc;u thổ s&ocirc;ng Hồng</a>, với địa h&igrave;nh bao gồm v&ugrave;ng đồng bằng trung t&acirc;m v&agrave; v&ugrave;ng đồi n&uacute;i ở ph&iacute;a bắc v&agrave; ph&iacute;a t&acirc;y th&agrave;nh phố. Với diện t&iacute;ch 3.358,6&nbsp;km2&nbsp;v&agrave; d&acirc;n số 8,05 triệu người (2019), H&agrave; Nội l&agrave; th&agrave;nh phố c&oacute; diện t&iacute;ch lớn nhất Việt Nam, đồng thời cũng l&agrave; th&agrave;nh phố đ&ocirc;ng d&acirc;n thứ hai v&agrave; c&oacute; mật độ d&acirc;n số cao thứ hai trong 63&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%E1%BB%89nh_th%C3%A0nh_Vi%E1%BB%87t_Nam\">đơn vị h&agrave;nh ch&iacute;nh cấp tỉnh</a>&nbsp;của Việt Nam, nhưng ph&acirc;n bố d&acirc;n số kh&ocirc;ng đồng đều. H&agrave; Nội c&oacute; 30&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Huy%E1%BB%87n_(Vi%E1%BB%87t_Nam)\">đơn vị h&agrave;nh ch&iacute;nh cấp huyện</a>, gồm 12 quận, 17 huyện v&agrave; 1 thị x&atilde;.</p>\r\n\r\n<p>H&agrave; Nội l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Kinh_%C4%91%C3%B4\">kinh đ&ocirc;</a>&nbsp;của hầu hết c&aacute;c vương triều phong kiến tại Việt Nam trước đ&acirc;y, do đ&oacute;, lịch sử H&agrave; Nội gắn liền với sự thăng trầm của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/L%E1%BB%8Bch_s%E1%BB%AD_Vi%E1%BB%87t_Nam\">lịch sử Việt Nam</a>&nbsp;qua c&aacute;c thời kỳ.<a href=\"https://vi.wikipedia.org/wiki/H%C3%A0_N%E1%BB%99i#cite_note-5\">[5]</a>&nbsp;Năm 1010, vua&nbsp;<a href=\"https://vi.wikipedia.org/wiki/L%C3%BD_Th%C3%A1i_T%E1%BB%95\">L&yacute; Th&aacute;i Tổ</a>, vị&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ho%C3%A0ng_%C4%91%E1%BA%BF\">ho&agrave;ng đế</a>&nbsp;đầu ti&ecirc;n của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_L%C3%BD\">nh&agrave; L&yacute;</a>, quyết định x&acirc;y dựng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Kinh_%C4%91%C3%B4\">kinh đ&ocirc;</a>&nbsp;mới ở v&ugrave;ng đất n&agrave;y với c&aacute;i t&ecirc;n &quot;Thăng Long&quot;. Trong suốt thời kỳ của c&aacute;c triều đại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_L%C3%BD\">L&yacute;</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_Tr%E1%BA%A7n\">Trần</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_H%E1%BA%ADu_L%C3%AA\">L&ecirc;</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_M%E1%BA%A1c\">Mạc</a>, kinh th&agrave;nh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C4%83ng_Long\">Thăng Long</a>&nbsp;l&agrave; trung t&acirc;m&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C4%83n_h%C3%B3a\">văn h&oacute;a</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Gi%C3%A1o_d%E1%BB%A5c\">gi&aacute;o dục</a>&nbsp;v&agrave; bu&ocirc;n b&aacute;n của cả nước. Khi&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_T%C3%A2y_S%C6%A1n\">T&acirc;y Sơn</a>&nbsp;rồi&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_Nguy%E1%BB%85n\">nh&agrave; Nguyễn</a>&nbsp;l&ecirc;n nắm quyền trị v&igrave;, kinh đ&ocirc; được chuyển về&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Hu%E1%BA%BF\">Huế</a>&nbsp;v&agrave; Thăng Long bắt đầu mang t&ecirc;n H&agrave; Nội từ năm 1831, dưới thời vua&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Minh_M%E1%BA%A1ng\">Minh Mạng</a>. Năm 1902, H&agrave; Nội trở th&agrave;nh thủ đ&ocirc; của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Li%C3%AAn_bang_%C4%90%C3%B4ng_D%C6%B0%C6%A1ng\">Li&ecirc;n bang Đ&ocirc;ng Dương</a>&nbsp;v&agrave; được người&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ph%C3%A1p\">Ph&aacute;p</a>&nbsp;x&acirc;y dựng,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Quy_ho%E1%BA%A1ch_%C4%91%C3%B4_th%E1%BB%8B\">quy hoạch</a>&nbsp;lại, được mệnh danh l&agrave; &quot;Tiểu Paris phương Đ&ocirc;ng&quot; thời bấy giờ. Trong thời kỳ&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Chi%E1%BA%BFn_tranh_Vi%E1%BB%87t_Nam\">chiến tranh Việt Nam</a>, H&agrave; Nội l&agrave; thủ đ&ocirc; của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam_D%C3%A2n_ch%E1%BB%A7_C%E1%BB%99ng_h%C3%B2a\">Việt Nam D&acirc;n chủ Cộng h&ograve;a</a>&nbsp;v&agrave; sau khi thống nhất tiếp tục l&agrave; thủ đ&ocirc; của nước Cộng h&ograve;a X&atilde; hội chủ nghĩa Việt Nam hiện nay. Đ&oacute; l&agrave; thủ đ&ocirc; của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%C3%B4ng_D%C6%B0%C6%A1ng\">Đ&ocirc;ng Dương</a>&nbsp;thuộc Ph&aacute;p 1887&ndash;1946 v&agrave; của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Mi%E1%BB%81n_B%E1%BA%AFc_(Vi%E1%BB%87t_Nam)\">miền Bắc Việt Nam</a>&nbsp;1954 &ndash; 1976 trước khi thống nhất miền Bắc v&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Mi%E1%BB%81n_Nam_(Vi%E1%BB%87t_Nam)\">miền Nam Việt Nam</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Hà Nội-Việt Nam', '[\"./assets/uploads/18436701616146fb66c47ab5.54509997.jpg\",\"./assets/uploads/6909688416146fb66c48210.33071835.jpg\"]', '2021-09-19 08:57:13'),
(4, 'Nha Trang', '<p><strong>Nha Trang</strong>&nbsp;l&agrave; một&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_ph%E1%BB%91\">th&agrave;nh phố</a>&nbsp;ven biển v&agrave; l&agrave; trung t&acirc;m ch&iacute;nh trị, kinh tế, văn h&oacute;a, khoa học kỹ thuật v&agrave; du lịch của tỉnh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Kh%C3%A1nh_H%C3%B2a\">Kh&aacute;nh H&ograve;a</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a>.</p>\r\n\r\n<p>Trước đ&acirc;y, v&ugrave;ng đất Nha Trang vốn thuộc về&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Chi%C3%AAm_Th%C3%A0nh\">Chi&ecirc;m Th&agrave;nh</a>, do đ&oacute; c&aacute;c di t&iacute;ch của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ng%C6%B0%E1%BB%9Di_Ch%C4%83m\">người Chăm</a>&nbsp;vẫn c&ograve;n tồn tại nhiều nơi ở Nha Trang. Nha Trang được Thủ tướng ch&iacute;nh phủ Việt Nam c&ocirc;ng nhận l&agrave; đ&ocirc; thị loại I v&agrave;o ng&agrave;y 22 th&aacute;ng 4 năm 2009. Nha Trang được mệnh danh l&agrave; h&ograve;n ngọc của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Bi%E1%BB%83n_%C4%90%C3%B4ng\">biển Đ&ocirc;ng</a>, Vi&ecirc;n ngọc xanh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nha_Trang#cite_note-3\">[3]</a>&nbsp;v&igrave; gi&aacute; trị thi&ecirc;n nhi&ecirc;n, vẻ đẹp cũng như kh&iacute; hậu của n&oacute;.<a href=\"https://vi.wikipedia.org/wiki/Nha_Trang#cite_note-4\">[4]</a></p>\r\n', 'Nha Trang-Khánh Hòa', '[\"./assets/uploads/12061829096146fc59e4a5d3.89910084.jpg\",\"./assets/uploads/8886956696146fc59e4ca63.99132776.jpg\"]', '2021-09-19 09:01:15'),
(6, 'Thái Nguyên', '<p><strong>Th&aacute;i Nguy&ecirc;n</strong>&nbsp;l&agrave; một tỉnh trung du thuộc v&ugrave;ng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%C3%B4ng_B%E1%BA%AFc_B%E1%BB%99\">Đ&ocirc;ng Bắc Bộ</a>, tỉnh lỵ l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A1i_Nguy%C3%AAn_(th%C3%A0nh_ph%E1%BB%91)\">th&agrave;nh phố Th&aacute;i Nguy&ecirc;n</a>, c&aacute;ch trung t&acirc;m thủ đ&ocirc;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/H%C3%A0_N%E1%BB%99i\">H&agrave; Nội</a>&nbsp;75&nbsp;km, v&agrave; l&agrave; tỉnh nằm trong&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Quy_ho%E1%BA%A1ch_v%C3%B9ng_th%E1%BB%A7_%C4%91%C3%B4_H%C3%A0_N%E1%BB%99i\">V&ugrave;ng thủ đ&ocirc; H&agrave; Nội</a>.</p>\r\n\r\n<p>Năm 2020, Th&aacute;i Nguy&ecirc;n l&agrave; đơn vị h&agrave;nh ch&iacute;nh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a>&nbsp;đ&ocirc;ng thứ 25 về số d&acirc;n, xếp thứ 15 về&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%E1%BB%95ng_s%E1%BA%A3n_ph%E1%BA%A9m_tr%C3%AAn_%C4%91%E1%BB%8Ba_b%C3%A0n_(GRDP)\">Tổng sản phẩm tr&ecirc;n địa b&agrave;n (GRDP)</a>. GRDP đạt 116.008 tỉ&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BB%93ng_(%C4%91%C6%A1n_v%E1%BB%8B_ti%E1%BB%81n_t%E1%BB%87)\">Đồng</a>&nbsp;(tương ứng với 5,0 tỉ USD), GRDP b&igrave;nh qu&acirc;n đầu người đạt 86 triệu đồng (tương ứng với 3.740 USD) đứng thứ 2 v&ugrave;ng trung du v&agrave; miền n&uacute;i ph&iacute;a bắc, đứng thứ 6 v&ugrave;ng thủ đ&ocirc; sau H&agrave; Nội, Bắc Ninh, Hải Dương, Bắc Giang v&agrave; Vĩnh Ph&uacute;c. Tốc độ tăng trưởng GRDP năm 2020 đạt 4,24&nbsp;%.<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A1i_Nguy%C3%AAn#cite_note-:017-2\">[2]</a></p>\r\n\r\n<p>T&iacute;nh sơ bộ đến năm 2020, d&acirc;n số tỉnh Th&aacute;i Nguy&ecirc;n l&agrave; 1.307.871 người, l&agrave; tỉnh đ&ocirc;ng d&acirc;n thứ 25 to&agrave;n quốc v&agrave; đứng thứ 3 c&aacute;c tỉnh trong v&ugrave;ng Trung du miền n&uacute;i ph&iacute;a Bắc. Sau 10 năm d&acirc;n số tỉnh Th&aacute;i Nguy&ecirc;n tăng 163.635 người, tỷ lệ tăng d&acirc;n số b&igrave;nh qu&acirc;n l&agrave; 1,36%/năm. T&iacute;nh từ thời điểm tổng điều tra d&acirc;n số v&agrave; nh&agrave; ở 01/04/2019 th&igrave; hết năm 2020 d&acirc;n số tỉnh Th&aacute;i Nguy&ecirc;n tăng th&ecirc;m 21.120 người.</p>\r\n\r\n<p>Tỉnh c&oacute; 434.111 người người cư tr&uacute; ở khu vực th&agrave;nh thị, chiếm 32% tổng d&acirc;n số; 876.484 người cư tr&uacute; ở khu vực n&ocirc;ng th&ocirc;n, chiếm 68%, tổng d&acirc;n số của tỉnh. Năm 2019, tỷ lệ d&acirc;n số sống ở th&agrave;nh thị của tỉnh đứng thứ 18 so với cả nước v&agrave; đứng đầu trong số c&aacute;c tỉnh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Trung_du_v%C3%A0_mi%E1%BB%81n_n%C3%BAi_ph%C3%ADa_B%E1%BA%AFc\">Trung du v&agrave; miền n&uacute;i ph&iacute;a Bắc</a>, đứng thứ hai trong&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C3%B9ng_th%E1%BB%A7_%C4%91%C3%B4_H%C3%A0_N%E1%BB%99i\">v&ugrave;ng Thủ đ&ocirc;</a>&nbsp;(chỉ sau Th&agrave;nh phố&nbsp;<a href=\"https://vi.wikipedia.org/wiki/H%C3%A0_N%E1%BB%99i\">H&agrave; Nội</a>). Theo kết quả của tổng điều tra d&acirc;n số nh&agrave; ở năm 2019 của Tổng cục Thống k&ecirc;, Th&aacute;i Nguy&ecirc;n l&agrave; địa phương đứng thứ 3 cả nước về tỷ lệ sở hữu &ocirc; t&ocirc; c&aacute; nh&acirc;n tr&ecirc;n hộ d&acirc;n với tỷ lệ 10,3%, chỉ xếp sau&nbsp;<a href=\"https://vi.wikipedia.org/wiki/H%C3%A0_N%E1%BB%99i\">H&agrave; Nội</a>&nbsp;v&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%C3%A0_N%E1%BA%B5ng\">Đ&agrave; Nẵng</a><a href=\"https://vi.wikipedia.org/wiki/Th%C3%A1i_Nguy%C3%AAn#cite_note-T%E1%BB%89nh_n%C3%A0o_c%C3%B3_t%E1%BB%89_l%E1%BB%87_d%C3%A2n_s%E1%BB%9F_h%E1%BB%AFu_%C3%B4_t%C3%B4_nhi%E1%BB%81u_nh%E1%BA%A5t_Vi%E1%BB%87t_Nam?-3\">[3]</a>&nbsp;v&agrave; đ&acirc;y cũng l&agrave; tỉnh c&oacute; tỉ lệ số hộ gia đ&igrave;nh sử dụng điện thoại đứng thứ 3 cả nước, chỉ xếp sau&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_ph%E1%BB%91_H%E1%BB%93_Ch%C3%AD_Minh\">Th&agrave;nh phố Hồ Ch&iacute; Minh</a>&nbsp;v&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/C%C3%A0_Mau\">C&agrave; Mau</a>.<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A1i_Nguy%C3%AAn#cite_note-T%E1%BB%89nh_n%C3%A0o_c%C3%B3_t%E1%BB%89_l%E1%BB%87_d%C3%A2n_s%E1%BB%9F_h%E1%BB%AFu_%C3%B4_t%C3%B4_nhi%E1%BB%81u_nh%E1%BA%A5t_Vi%E1%BB%87t_Nam?-3\">[3]</a></p>\r\n\r\n<p>Theo kết quả Chỉ số cải c&aacute;ch h&agrave;nh ch&iacute;nh (PCI) năm 2019 của c&aacute;c tỉnh, th&agrave;nh phố, Th&aacute;i Nguy&ecirc;n xếp vị tr&iacute; thứ 12/63 với tổng số 67,71 điểm, tăng 6 bậc so với năm 2018. Đứng đầu v&ugrave;ng Trung du miền n&uacute;i ph&iacute;a Bắc.</p>\r\n\r\n<p>Chỉ số hiệu quả quản trị v&agrave; h&agrave;nh ch&iacute;nh c&ocirc;ng cấp tỉnh ở Việt Nam (Chỉ số PAPI) năm 2020, tỉnh Th&aacute;i Nguy&ecirc;n đạt tổng điểm 46.471, thuộc nh&oacute;m 16 tỉnh đạt điểm cao nhất (đứng thứ 3 cả nước), sau tỉnh Quảng Ninh (48.811 điểm) v&agrave; tỉnh Đồng Th&aacute;p (46.961 điểm). 6/8 chỉ số nội dung PAPI của tỉnh nằm trong nh&oacute;m đạt điểm cao nhất. Cụ thể: Chỉ số Thủ tục h&agrave;nh ch&iacute;nh c&ocirc;ng đạt 7.656 điểm; Chỉ số kiểm so&aacute;t tham nhũng trong khu vực c&ocirc;ng đạt 7.278 điểm; Chỉ số c&ocirc;ng khai, minh bạch trong việc ra quyết định đạt 6.322 điểm; Chỉ số tham gia của người d&acirc;n cấp cơ sở đạt 6.216 điểm; Chỉ số tr&aacute;ch nhiệm giải tr&igrave;nh với người d&acirc;n đạt 5.335 v&agrave; Chỉ số quản trị điện tử đạt 3.463 điểm. Th&aacute;i Nguy&ecirc;n cũng l&agrave; tỉnh duy nhất c&oacute; tới 6 chỉ số nội dung tăng điểm phần trăm so với năm 2019.</p>\r\n', 'Thái Nguyên-Việt Nam', '[\"./assets/uploads/661198366614d8c19e6e615.28906980.jpg\"]', '2021-09-24 08:28:11'),
(7, 'Huế', '<p><strong>Huế</strong>&nbsp;l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%C3%A0nh_ph%E1%BB%91\">th&agrave;nh phố</a>&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%E1%BB%89nh_l%E1%BB%B5\">tỉnh lỵ</a>&nbsp;của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%E1%BB%89nh\">tỉnh</a>&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%E1%BB%ABa_Thi%C3%AAn_Hu%E1%BA%BF\">Thừa Thi&ecirc;n Huế</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a>. Huế từng l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/C%E1%BB%91_%C4%91%C3%B4_Hu%E1%BA%BF\">kinh đ&ocirc; (cố đ&ocirc; Huế)</a>&nbsp;của Việt Nam thời phong kiến dưới&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_T%C3%A2y_S%C6%A1n\">triều T&acirc;y Sơn</a>&nbsp;(1788&ndash;1801) v&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_Nguy%E1%BB%85n\">nh&agrave; Nguyễn</a>&nbsp;(1802&ndash;1945). Hiện nay, th&agrave;nh phố l&agrave; một trong những trung t&acirc;m về&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C4%83n_h%C3%B3a\">văn h&oacute;a</a>&nbsp;&ndash;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Du_l%E1%BB%8Bch\">du lịch</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Y_t%E1%BA%BF\">y tế</a>&nbsp;chuy&ecirc;n s&acirc;u,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Gi%C3%A1o_d%E1%BB%A5c\">gi&aacute;o dục</a>&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%C3%A0o_t%E1%BA%A1o\">đ&agrave;o tạo</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Khoa_h%E1%BB%8Dc\">khoa học</a>&nbsp;<a href=\"https://vi.wikipedia.org/wiki/C%C3%B4ng_ngh%E1%BB%87\">c&ocirc;ng nghệ</a>&nbsp;của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Mi%E1%BB%81n_Trung\">Miền Trung</a>&nbsp;-&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%C3%A2y_Nguy%C3%AAn\">T&acirc;y Nguy&ecirc;n</a>&nbsp;v&agrave; cả nước. Những địa danh nổi bật l&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/S%C3%B4ng_H%C6%B0%C6%A1ng\">s&ocirc;ng Hương</a>&nbsp;v&agrave; những di sản để lại của triều đại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Phong_ki%E1%BA%BFn\">phong kiến</a>, Th&agrave;nh phố c&oacute; năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Danh_hi%E1%BB%87u_UNESCO_%E1%BB%9F_Vi%E1%BB%87t_Nam\">danh hiệu UNESCO ở Việt Nam</a>:&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Qu%E1%BA%A7n_th%E1%BB%83_di_t%C3%ADch_C%E1%BB%91_%C4%91%C3%B4_Hu%E1%BA%BF\">Quần thể di t&iacute;ch Cố đ&ocirc; Huế</a>&nbsp;(1993),&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A3_nh%E1%BA%A1c_cung_%C4%91%C3%ACnh_Hu%E1%BA%BF\">Nh&atilde; nhạc cung đ&igrave;nh Huế</a>&nbsp;(2003),&nbsp;<a href=\"https://vi.wikipedia.org/wiki/M%E1%BB%99c_b%E1%BA%A3n_tri%E1%BB%81u_Nguy%E1%BB%85n\">Mộc bản triều Nguyễn</a>&nbsp;(2009),&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ch%C3%A2u_b%E1%BA%A3n_tri%E1%BB%81u_Nguy%E1%BB%85n\">Ch&acirc;u bản triều Nguyễn</a>&nbsp;(2014) v&agrave; Hệ thống thơ văn tr&ecirc;n kiến tr&uacute;c cung đ&igrave;nh Huế (2016). Ngo&agrave;i ra, Huế c&ograve;n l&agrave; một trong những địa phương c&oacute; di sản h&aacute;t b&agrave;i ch&ograve;i đ&atilde; được c&ocirc;ng nhận l&agrave; di sản văn h&oacute;a phi vật thể của nh&acirc;n loại.</p>\r\n', 'Huế - Việt Nam', '[\"./assets/uploads/533575430614d8caa7178f9.16432602.jpg\"]', '2021-09-24 08:30:36'),
(8, 'Quảng Ninh', '<p><strong>Quảng Ninh</strong>&nbsp;l&agrave; tỉnh ven biển thuộc&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C3%B9ng_%C4%90%C3%B4ng_B%E1%BA%AFc_(Vi%E1%BB%87t_Nam)\">v&ugrave;ng Đ&ocirc;ng Bắc Việt Nam</a>. Theo quy hoạch ph&aacute;t triển&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Kinh_t%E1%BA%BF\">kinh tế</a>, Quảng Ninh vừa thuộc&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C3%B9ng_kinh_t%E1%BA%BF_tr%E1%BB%8Dng_%C4%91i%E1%BB%83m_B%E1%BA%AFc_b%E1%BB%99\">v&ugrave;ng kinh tế trọng điểm ph&iacute;a bắc</a>&nbsp;vừa thuộc&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C3%B9ng_duy%C3%AAn_h%E1%BA%A3i_B%E1%BA%AFc_B%E1%BB%99\">V&ugrave;ng duy&ecirc;n hải Bắc Bộ</a>. Đ&acirc;y l&agrave; tỉnh khai th&aacute;c&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Than_%C4%91%C3%A1\">than đ&aacute;</a>&nbsp;ch&iacute;nh của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Vi%E1%BB%87t_Nam\">Việt Nam</a>, c&oacute;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%E1%BB%8Bnh_H%E1%BA%A1_Long\">vịnh Hạ Long</a>&nbsp;l&agrave; di sản, kỳ quan thi&ecirc;n nhi&ecirc;n thế giới v&agrave; l&agrave; qu&ecirc; gốc của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%C3%A0_Tr%E1%BA%A7n\">Nh&agrave; Trần</a>&nbsp;- một trong những triều đại h&ugrave;ng mạnh nhất lịch sử Việt Nam.</p>\r\n', 'Quảng Ninh - Việt Nam', '[\"./assets/uploads/211428776614d91d7274f84.15144984.jpeg\",\"./assets/uploads/1185904590614d91d72755a5.25123500.jpeg\"]', '2021-09-24 08:52:40');

