INSERT INTO `rooms` (`MaPhong`, `MaKS`, `tenPhong`, `content`, `gia`, `image`, `createDate`) VALUES
(2, 2, 'Phòng Deluxe Giường Đôi/2 Giường Đơn - Chỉ Phòng Nghỉ', '<p><a href=\"https://i0.wp.com/s1.uphinh.org/2021/09/19/Selection_008.png\"><img alt=\"\" src=\"https://i0.wp.com/s1.uphinh.org/2021/09/19/Selection_008.png\" style=\"height:97px; width:343px\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>K&iacute;ch thước ph&ograve;ng&nbsp;</h2>\r\n\r\n<p>28 m&sup2;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>This twin/double room has a electric kettle, minibar and air conditioning.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Trong ph&ograve;ng tắm ri&ecirc;ng của bạn:</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Áo choàng tắm</li>\r\n	<li>Nh&agrave; vệ sinh</li>\r\n	<li>B&ocirc;̀n tắm hoặc Vòi sen</li>\r\n	<li>Khăn tắm</li>\r\n	<li>Dép</li>\r\n	<li>Máy s&acirc;́y tóc</li>\r\n	<li>Toilet phụ</li>\r\n	<li>Giấy vệ sinh</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Hướng tầm nh&igrave;n:</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Nh&igrave;n ra th&agrave;nh phố</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Tiện nghi ph&ograve;ng: ​</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>C&aacute;c tầng tr&ecirc;n đi l&ecirc;n bằng thang m&aacute;y</li>\r\n	<li>C&aacute;c tầng tr&ecirc;n chỉ l&ecirc;n được bằng cầu thang</li>\r\n	<li>Xe lăn c&oacute; thể đi đến mọi nơi trong to&agrave;n bộ khu&ocirc;n vi&ecirc;n</li>\r\n	<li>Đồng hồ b&aacute;o thức</li>\r\n	<li>Tủ hoặc ph&ograve;ng để quần &aacute;o</li>\r\n	<li>M&aacute;y pha tr&agrave;/c&agrave; ph&ecirc;</li>\r\n	<li>Minibar</li>\r\n	<li>Điều h&ograve;a kh&ocirc;ng kh&iacute;</li>\r\n	<li>Két an toàn</li>\r\n	<li>H&ecirc;̣ th&ocirc;́ng sưởi</li>\r\n	<li>Ấm đun nước điện</li>\r\n	<li>Bàn làm vi&ecirc;̣c</li>\r\n	<li>Khu vực ti&ecirc;́p khách</li>\r\n	<li>TV</li>\r\n	<li>Đi&ecirc;̣n thoại</li>\r\n	<li>Truy&ecirc;̀n hình v&ecirc;̣ tinh</li>\r\n	<li>TV m&agrave;n h&igrave;nh phẳng</li>\r\n	<li>Ổ điện gần giường</li>\r\n	<li>Gi&aacute; treo quần &aacute;o</li>\r\n	<li>Dịch vụ b&aacute;o thức</li>\r\n	<li>Dịch vụ b&aacute;o thức<